import React, { useRef, useEffect} from "react";
import * as d3 from "d3";



const Mainplot = (props) => {

	const splotSvg = useRef(null);
  const barplotSvg = useRef(null);
  const svgSize = props.margin * 2 + props.size;

  // TODO

	//dino plot
	useEffect(() => {
    // TODO

		//dataset
		let datasetX = props.data.map(d => d[0]);
		let datasetY = props.data.map(d => d[1]);

		//define axisX
		let linearX = d3.scaleLinear().domain([d3.min(datasetX), d3.max(datasetX)]).range([0, props.size]);
		let axisX = d3.axisBottom(linearX);

		//define axisY
		let linearY = d3.scaleLinear().domain([d3.min(datasetY), d3.max(datasetY)]).range([props.size, 0]);
		let axisY = d3.axisLeft(linearY);

		//append axisX
		const svg = d3.select(splotSvg.current);
		svg.append("g")
	 		 .attr("transform", `translate(${props.margin},${props.size + props.margin})`)
			 .call(axisX)

		//append axisY
		svg.append("g")
			 .attr("transform", `translate(${props.margin},${props.margin})`)
			 .call(axisY)

		//append dot
		const dotSvg = svg.append("g")
											.attr("transform", `translate(${props.margin},${props.margin})`)
		dotSvg.selectAll(".dataCircle")
					.data(props.data)
					.enter()
					.append("circle")
					.attr("class", (_,i) => `circle${i}`)
					.attr("r", props.radius)
					.attr("cx", d => linearX(d[0]))
					.attr("cy", d => linearY(d[1]))


		//append brush
		const brush = d3.brush()
      .extent([[0, 0], [props.size, props.size]])
      .on("start brush end", brushed);

		const brushSvg = svg.append("g")
												.attr("transform", `translate(${props.margin},${props.margin})`)
												.call(brush)


		function brushed(event) {
    let selection = event.selection;
		let [[x0, y0],[x1, y1]] = selection;
		console.log(`x0:${x0},y0:${y0},x1:${x1},y1:${y1}`)
		// brush none dot
    if (selection === null) {
      d3.selectAll("circle").attr("fill", "black");
      return;
    }
		//transfer data
		const dataT = props.data.map((d, i)=>{
			d[0]=linearX(parseFloat(d[0]));
			d[1]=linearY(parseFloat(d[1]));
			if(x0 <= dataT[0] && dataT[0] <= x1 && y0 <= dataT[1] && dataT[1] <= y1)
			{
				const selected = d3.select(`circle${i}`).attr("fill", "red");
			}
		});
    // //const sx = selection.map(x.invert);

    // circle.attr("fill", dataT => x0 <= dataT[0] && dataT[0] <= x1 && y0 <= dataT[1] && dataT[1] <= y1 ? "red" : "black");
    // //d3.select(this).call(brushHandle, selection);
  }
	// eslint-disable-next-line
	}, []);

	//bar plot
	useEffect(()=>{

		//define axisX
		// let linearX = d3.scaleLinear().domain([d3.min(datasetX), d3.max(datasetX)]).range([0, props.size]);
		// let axisX = d3.axisBottom(linearX);

		// //define axisY
		// let linearY = d3.scaleLinear().domain([d3.min(datasetY), d3.max(datasetY)]).range([props.size, 0]);
		// let axisY = d3.axisLeft(linearY);

	},[])

	return (
		<div>
			<svg ref={splotSvg} width={svgSize} height={svgSize}> 
			</svg>
      <svg ref={barplotSvg} width={svgSize} height={svgSize}> 
			</svg>

		</div>
	)
};

export default Mainplot;
